    state = {  }
    render() { 
        return ( 
            <>
               <div className="container">
        <div className="calcu" id="calcu">
            <div className="bar upper" ><div id="divbtn"><button className="sqbtn" onClick="changebg('rgb(118, 0, 0)')" id="navbtn1"></button> <button onClick="changebg('rgb(189, 164, 0)')"className="sqbtn" id="navbtn2"></button> <button onClick="changebg('rgb(0, 65, 0)')" className="sqbtn" id="navbtn3"></button></div>
            <div>
                <label className="switch">
                  <input type="checkbox" onClick="changebg()" checked />
                  <span className="slider round"></span>
                </label></div>
            </div>
            <table>
                <thead id="x1">
                       <tr>
                        <td colspan="4">
                            <input id="deep" className="upper input" type="text"  value="00" disabled />
                       </td>
                    </tr>
                </thead>
                <tbody id="tbody">
                    <tr>
                    <Button fun={this.copyr} cls="sqbtn reset sp1" txt="C"/>
                    <Button fun={this.add} cls="sqbtn btn sp1" txt="&copy;"/>
                    <Button fun={this.add} cls="sqbtn btn sp1" txt="%"/>
                    <Button fun={this.add} cls="sqbtn btn sp1" txt="/"/>
                    </tr>
                   </tbody>
            </table>
        </div>
    </div>
            </>
         );
    }
}

export default Calci;
